(function() {
  module.exports = function(fn) {
    var cache, memoized;
    cache = {};
    return memoized = function(key) {
      if (!cache[key]) {
        cache[key] = fn.apply(this, arguments);
        cache[key].fail(function() {
          return delete cache[key];
        });
      }
      return cache[key];
      return memoized;
    };
  };

}).call(this);

