(function() {
  var MemoizePromise, Replay;

  MemoizePromise = require("../main");

  Replay = function(values) {
    var fn, index;
    index = -1;
    return fn = function() {
      index += 1;
      return values[index];
    };
  };

  describe("MemoizePromise", function() {
    it("should memoize promises", function(done) {
      var called, fn, handler, resolved;
      called = 0;
      fn = MemoizePromise(function(v) {
        var deferred;
        deferred = Q.defer();
        called += 1;
        deferred.resolve(v);
        return deferred.promise;
      });
      resolved = 0;
      handler = function(v) {
        assert.equal(v, "a");
        assert.equal(called, 1);
        resolved += 1;
        if (resolved === 2) {
          return done();
        }
      };
      fn("a").then(handler);
      return fn("a").then(handler);
    });
    it("should void cache on failure", function(done) {
      var called, calledReplay, fn, handler, rejected;
      called = 0;
      fn = MemoizePromise(function(v) {
        var deferred;
        deferred = Q.defer();
        called += 1;
        deferred.reject(v);
        return deferred.promise;
      });
      rejected = 0;
      calledReplay = Replay([1, 2, 3]);
      handler = function(v) {
        assert.equal(v, "a");
        assert.equal(called, calledReplay());
        rejected += 1;
        if (rejected === 3) {
          return done();
        }
      };
      fn("a").then(function() {
        return assert(false);
      }, handler).done();
      return setTimeout(function() {
        fn("a").then(function() {
          return assert(false);
        }, handler).done();
        return setTimeout(function() {
          return fn("a").then(function() {
            return assert(false);
          }, handler).done();
        });
      });
    });
    return it("should cache after a failure", function(done) {
      var called, calledReplay, completed, fn, handler;
      called = 0;
      fn = MemoizePromise(function(v) {
        var deferred;
        deferred = Q.defer();
        called += 1;
        if (called === 2) {
          deferred.resolve(v);
        } else {
          deferred.reject(v);
        }
        return deferred.promise;
      });
      completed = 0;
      calledReplay = Replay([1, 2, 2]);
      handler = function(v) {
        assert.equal(v, "a");
        assert.equal(called, calledReplay());
        completed += 1;
        if (completed === 3) {
          return done();
        }
      };
      fn("a").then(function() {
        return assert(false);
      }, handler).done();
      return setTimeout(function() {
        fn("a").then(handler, function() {
          return assert(false);
        }).done();
        return setTimeout(function() {
          return fn("a").then(handler, function() {
            return assert(false);
          }).done();
        });
      });
    });
  });

}).call(this);
